package designPattern.java;

//clone()을 사용하기 위해 Cloneable interface를 구현하고 Object에 있는 clone()메소드를 재정의해야 한다.
public class CloneAbleImpl implements Cloneable{
	private String Name;
	
	public CloneAbleImpl(String _name){
		this.Name = _name;
	}
	
	public String getName(){
		return Name;
	}

	@Override
	protected Object clone() throws CloneNotSupportedException {
		// TODO Auto-generated method stub
		return super.clone();
	}
	
	
}
